import React from "react";
import css from "./style.module.css";
import Burger from "../../components/Burger";
import Button from "../../components/General/Button";
import { useNavigate, useSearchParams, Route, Routes } from "react-router-dom";
import { ContactData } from "../../components/ContactData";

export function ShippingPage() {
  const [searchParams] = useSearchParams();
  const ingredients = {};
  let price = 0;
  for (const [key, value] of searchParams.entries()) {
    if (key !== "price") ingredients[key] = parseInt(value, 10);
    else price = value;
  }
  const navigate = useNavigate();
  const goBack = () => {
    navigate(-1);
  };
  const goContact = () => {
    navigate("contact", { replace: true });
  };

  return (
    <ShippingPage1
      query={ingredients}
      goBack={goBack}
      goContact={goContact}
      price={price}
    />
  );
}

class ShippingPage1 extends React.Component {
  state = {
    ingredients: {
      bacon: 0,
      cheese: 0,
      meat: 2,
      salad: 1,
    },
    price: 0,
  };
  componentDidMount() {
    const { query } = this.props;
    const { price } = this.props;
    this.setState({ ingredients: query, price });
  }

  cancelOrder = () => {
    const { goBack } = this.props;
    goBack();
  };

  render() {
    return (
      <div className={css.ShippingPage}>
        <p style={{ fontSize: "24px" }}>
          <strong>Таны захиалга амттай байх болно гэж найдаж байна...</strong>
        </p>
        <p style={{ fontSize: "24px" }}>
          <strong>Дүн: {this.state.price} ₮</strong>
        </p>
        <Burger ingredients={this.state.ingredients} />
        <Button
          text="ЗАХИАЛГЫГ ЦУЦЛАХ"
          clicked={this.cancelOrder}
          btnType="Danger"
        />
        <Button
          text="ХҮРГЭЛТИЙН МЭДЭЭЛЭЛ ОРУУЛАХ"
          clicked={this.props.goContact}
          btnType="Success"
        />

        <Routes>
          <Route path="contact" element={<ContactData />} />
        </Routes>
      </div>
    );
  }
}
