import React from "react";
import BurgerIngredient from "../BurgerIngredient";
import css from "./style.module.css";
const Burger = (props) => {
  // orts irsen objectyn bub utgyg massiv baidlaar oruulj  ogon
  const items = Object.entries(props.ingredients);

  let content = [];
  //map el bolgony huwid guune
  //el dtrh 1 indexte elemnty toonoos hmaarc dtroo dhij dawtna
  items.map((el) => {
    for (let i = 0; i < el[1]; i++)
      content.push(<BurgerIngredient key={`${el[0]}${i + 1}`} type={el[0]} />);
  });
  if (content.length === 0)
    content = <p>Хачиртай талхныхаа орцыг сонгоно уу...</p>;

  return (
    <div className={css.Burger}>
      <BurgerIngredient type="bread-top" />
      {content}
      <BurgerIngredient type="bread-bottom" />
    </div>
  );
};
export default Burger;
