import React from "react";
import css from "./style.module.css";
const Modal = (props) => {
  return (
    <div
      onClick={props.closeConfrimModal}
      style={{
        transform: props.show ? "translateY(0)" : "translateY(-100vh)",
        opacity: props.show ? "1" : "0",
      }}
      className={css.Modal}
    >
      {props.children}
    </div>
  );
};
export default Modal;
