import React from "react";
import BuildControl from "../BuildControl";
import css from "./style.module.css";
const BuildControls = (props) => {
  return (
    <div className={css.BuildControls}>
      <p>
        Бургерийн үнэ: <strong>{props.price}</strong>
      </p>
      {/* //objectyg massiv bolgoj baigaad ashiglah */}
      {Object.keys(props.ingredientsName).map((el) => (
        <BuildControl
          key={el}
          addIngredient={props.addIngredient}
          minusIngredient={props.minusIngredient}
          disabledMinus={props.disabledIngredientsMinus}
          disabledAdd={props.disabledIngredientsAdd}
          type={el}
          ingredient={props.ingredientsName[el]}
        />
      ))}
      <button
        onClick={props.showConfirmModal}
        disabled={props.disabledPurchasing}
        className={css.OrderButton}
      >
        Захиалах
      </button>
    </div>
  );
};
export default BuildControls;
