import React from "react";
import css from "./style.module.css";
import Burger from "../../components/Burger";
import Button from "../../components/General/Button";
import {
  useNavigate,
  useSearchParams,
  Route,
  Routes,
  useLocation,
} from "react-router-dom";
import { ContactData } from "../../components/ContactData";

export function ShippingPage() {
  const [searchParams] = useSearchParams();
  const ingredients = {};
  for (const [key, value] of searchParams.entries()) {
    ingredients[key] = parseInt(value, 10);
  }
  const navigate = useNavigate();
  const location = useLocation();
  const goBack = () => {
    navigate(-1);
  };
  const goContact = () => {
    navigate("/ship/contact");
  };

  return (
    <ShippingPage1
      query={ingredients}
      goBack={goBack}
      goContact={goContact}
      navigate={navigate}
      location={location}
    />
  );
}

class ShippingPage1 extends React.Component {
  state = {
    ingredients: {
      bacon: 0,
      cheese: 0,
      meat: 2,
      salad: 1,
    },
  };
  componentDidMount() {
    const { query } = this.props;
    this.setState({ ingredients: query });
  }

  goBack = () => {
    const { goBack } = this.props;
    goBack();
  };
  showContactData = () => {
    const { navigate } = this.props;
    navigate("/ship/contact");
  };

  render() {
    const { location } = this.props;
    const showContactData = location.pathname === "/ship/contact";
    return (
      <div className={css.ShippingPage}>
        <p style={{ fontSize: "24px" }}>
          <strong>Таны захиалга амттай байх болно гэж найдаж байна...</strong>
        </p>
        <Burger ingredients={this.state.ingredients} />
        <Button
          text="ЗАХИАЛГЫГ ЦУЦЛАХ"
          clicked={this.goBack}
          btnType="Danger"
        />
        <Button
          text="ХҮРГЭЛТИЙН МЭДЭЭЛЭЛ ОРУУЛАХ"
          clicked={this.showContactData}
          btnType="Success"
        />
        {showContactData && <ContactData />}
        {/* <Routes>
          <Route path="/ship/contact" element={<ContactData />} />
        </Routes> */}
      </div>
    );
  }
}
