import React from "react";
import css from "./style.module.css";
import Burger from "../../components/Burger";
import { useSearchParams } from "react-router-dom";
export class ShippingPage extends React.Component {
  state = {
    ingredients: {
      bacon: 0,
      cheese: 0,
      meat: 2,
      salad: 1,
    },
  };
  componentDidMount() {
    if (this.props.location) {
      const params = new URLSearchParams(this.props.location.search);
      const ingredients = {
        bacon: parseInt(params.get("bacon")) || 0,
        cheese: parseInt(params.get("cheese")) || 0,
        meat: parseInt(params.get("meat")) || 0,
        salad: parseInt(params.get("salad")) || 0,
      };
      this.setState({ ingredients });
    } else {
      console.error("Location prop is undefined:", this.props.location);
      // Handle error or set default state if necessary
    }
  }

  render() {
    return (
      <div>
        <Burger ingredients={this.state.ingredients} />
      </div>
    );
  }
}
