import React, { Component } from "react";
import { connect } from "react-redux";
import Burger from "../../components/Burger";
import BuildControls from "../../components/BuildControls";
import Modal from "../../components/General/Modal";
import OrderSummary from "../../components/OrderSummary";
import axios from "../../axios-orders";
import Spinner from "../../components/General/Spinner";
import { useNavigate } from "react-router-dom";
function BurgerPage(props) {
  const navigate = useNavigate();
  return (
    <BurgerPage1
      navigate={navigate}
      burgeriinOrts={props.burgeriinOrts}
      burgereesOrtsHas={props.burgereesOrtsHas}
      burgertOrtsNem={props.burgertOrtsNem}
      niitUne={props.niitUne}
    />
  );
}
//un hezee c oorclgdq ucir
const INGTREDIENT_PRICE = {
  salad: 1500,
  cheese: 1000,
  bacon: 5000,
  meat: 8000,
};
const INGTREDIENT_NAME = {
  bacon: "Гахайн мах",
  cheese: "Бяслаг",
  meat: "Үхрийн мах",
  salad: "Салад",
};
//Dotroo state hadgaldag class torlin component
//ucir n orts zereg ym hadgalna
class BurgerPage1 extends Component {
  state = {
    ingredients: {
      bacon: 0,
      cheese: 0,
      meat: 0,
      salad: 0,
    },
    totalPrice: 1000,
    purchasing: false,
    confirmOrder: false,
    latCustomerName: null,
    loading: false,
  };
  //render hiij bugd hargdsni daraa
  componentDidMount = () => {
    console.log(this.props);
    this.setState({ loading: true });
    axios
      .get("./orders.json")
      .then((response) => {
        //objectyg massiv bolgoj oruulah
        let arr = Object.entries(response.data);
        //dtrh oggdlyg esrgeer n erguulh
        arr = arr.reverse();
        const lastOrder = arr[0][1];
        this.setState({
          ingredients: lastOrder.ingredients,
          totalPrice: lastOrder.price,
          latCustomerName: lastOrder.address.name,
        });
      })
      .catch((err) => console.log(err))
      .finally(() => {
        this.setState({ loading: false });
      });
  };

  continueOrder = () => {
    const params = [];
    for (let ingredients in this.props.burgeriinOrts) {
      params.push(ingredients + "=" + this.props.burgeriinOrts[ingredients]);
    }
    params.push("price=" + this.props.niitUne);
    const query = params.join("&");
    const { navigate } = this.props;
    navigate({
      pathname: "./ship",
      search: `?${query}`,
    });

    this.closeConfirmModal();
  };
  showConfirmModal = () => {
    this.setState({ confirmOrder: true });
  };
  closeConfirmModal = () => {
    this.setState({ confirmOrder: false });
  };
  //setState deer huucin obectyg zaalt orj irwel shineer render hidgu
  addIngredient = (type) => {
    // ... buyu spread operator ar talin objectyg zadlaad shiner yg adilhn bicn
    const newIngredients = { ...this.props.burgeriinOrts };
    if (this.props.burgeriinOrts[type] !== 5) {
      newIngredients[type]++;
      const newPrice = this.props.niitUne + INGTREDIENT_PRICE[type];
      this.setState({
        purchasing: true,
        totalPrice: newPrice,
        ingredients: newIngredients,
      });
    }
  };
  minusIngredient = (type) => {
    const newIngredients = { ...this.props.burgeriinOrts };
    if (this.props.burgeriinOrts[type] !== 0) {
      newIngredients[type]--;
      const newPrice = this.props.niitUne - INGTREDIENT_PRICE[type];

      this.setState({
        purchasing: newPrice > 1000,
        totalPrice: newPrice,
        ingredients: newIngredients,
      });
    }
  };

  render() {
    const disabledIngredientsMinus = { ...this.props.burgeriinOrts };
    const disabledIngredientsAdd = { ...this.props.burgeriinOrts };
    //object dotor bdag propertygaar datwalt hiih
    for (let key in disabledIngredientsMinus) {
      disabledIngredientsMinus[key] = disabledIngredientsMinus[key] === 0;
    }
    for (let key in disabledIngredientsAdd) {
      disabledIngredientsAdd[key] = disabledIngredientsAdd[key] === 5;
    }

    return (
      <div>
        <Modal
          closeConfirmModal={this.closeConfirmModal}
          show={this.state.confirmOrder}
        >
          {this.state.loading ? (
            <Spinner />
          ) : (
            <OrderSummary
              onCancel={this.closeConfirmModal}
              onContinue={this.continueOrder}
              price={this.props.niitUne}
              ingredientsName={INGTREDIENT_NAME}
              ingredients={this.props.burgeriinOrts}
            />
          )}
        </Modal>
        {this.state.loading && <Spinner />}
        <p style={{ width: "100%", textAlign: "center", fontSize: "28px" }}>
          Сүүлчийн захиалагчын нэр: {this.state.latCustomerName}
        </p>
        <Burger ingredients={this.props.burgeriinOrts} />
        <BuildControls
          showConfirmModal={this.showConfirmModal}
          ingredientsName={INGTREDIENT_NAME}
          disabledPurchasing={!this.state.purchasing}
          price={this.props.niitUne}
          disabledIngredientsMinus={disabledIngredientsMinus}
          disabledIngredientsAdd={disabledIngredientsAdd}
          addIngredient={this.props.burgertOrtsNem}
          minusIngredient={this.props.burgereesOrtsHas}
        />
      </div>
    );
  }
}
//reducer totor bgaa state-ees awj bgaa
const a = (state) => {
  return {
    burgeriinOrts: state.ingredients,
    niitUne: state.totalPrice,
  };
};
const b = (dispatch) => {
  return {
    burgertOrtsNem: (ortsniNer) => dispatch({ type: "ADD_INGREDIENT" }),
    burgereesOrtsHas: (ortsniNer) => dispatch({ type: "REMOVE_INGREDIENT" }),
  };
};
//connect n ooroo funct butsaan ter funct n BurgerPage so burger pagin props ruu a, b -g damjuulj ogj bgaa
export default connect(a, b)(BurgerPage);
