const initialState = {
  ingredients: {
    bacon: 0,
    cheese: 0,
    meat: 0,
    salad: 0,
  },
  totalPrice: 1000,
};
const INGTREDIENT_PRICE = {
  salad: 1500,
  cheese: 1000,
  bacon: 5000,
  meat: 8000,
};
const reducer = (state = initialState, action) => {
  if (action.type === "ADD_INGREDIENT") {
    return {
      ingredients: {
        ...state.ingredients,
        [action.nemehOrts]: state.ingredients[action.nemehOrts] + 1,
        totalPrice: INGTREDIENT_PRICE[action.nemehOrts],
      },
    };
  } else if (action.type === "REMOVE_INGREDIENT") {
    return {
      ingredients: {
        ...state.ingredients,
        [action.hasahOrts]: state.ingredients[action.hasahOrts] - 1,
      },
      totalPrice: state.totalPrice + INGTREDIENT_PRICE[action.nemehOrts],
    };
  }
  return state;
};
export default reducer;
