import React, { Component } from "react";
import Burger from "../../components/Burger";
import BuildControls from "../../components/BuildControls";
import Modal from "../../components/General/Modal";
import OrderSummary from "../../components/OrderSummary";
import axios from "../../axios-orders";

//un hezee c oorclgdq ucir
const INGTREDIENT_PRICE = {
  salad: 1500,
  cheese: 1000,
  bacon: 5000,
  meat: 8000,
};
const INGTREDIENT_NAME = {
  bacon: "Гахайн мах",
  cheese: "Бяслаг",
  meat: "Үхрийн мах",
  salad: "Салад",
};
//Dotroo state hadgaldag class torlin component
//ucir n orts zereg ym hadgalna
class BurgerPage extends Component {
  state = {
    ingredients: {
      bacon: 0,
      cheese: 0,
      meat: 0,
      salad: 0,
    },
    totalPrice: 1000,
    purchasing: false,
    confirmOrder: false,
  };
  //render hiij bugd hargdsni daraa
  componentDidMount = () => {
    axios.get("./orders.json").then((response) => {
      //objectyg massiv bolgoj oruulah
      const arr = Object.entries(response.data);
      arr.forEach((el) => {
        console.log(el[1].address.name + "===>" + el[1].price);
      });
      const lastOrder = arr[arr.length - 1];
      console.log(lastOrder);
    });
  };

  continueOrder = () => {
    const order = {
      ingredients: this.state.ingredients,
      price: this.state.totalPrice,
      address: {
        name: "Saraa",
        city: "UB",
        street: "10-r horoolol 23-12",
      },
    };
    // axios iin buh funct n promise ashigladag
    axios.post("/orders.json", order).then((response) => {
      alert("Amjilttai hadgallaa");
    });
  };
  showConfirmModal = () => {
    this.setState({ confirmOrder: true });
  };
  closeConfirmModal = () => {
    this.setState({ confirmOrder: false });
  };
  //setState deer huucin obectyg zaalt orj irwel shineer render hidgu
  addIngredient = (type) => {
    // ... buyu spread operator ar talin objectyg zadlaad shiner yg adilhn bicn
    const newIngredients = { ...this.state.ingredients };
    if (this.state.ingredients[type] !== 5) {
      newIngredients[type]++;
      const newPrice = this.state.totalPrice + INGTREDIENT_PRICE[type];
      this.setState({
        purchasing: true,
        totalPrice: newPrice,
        ingredients: newIngredients,
      });
    }
  };
  minusIngredient = (type) => {
    const newIngredients = { ...this.state.ingredients };
    if (this.state.ingredients[type] !== 0) {
      newIngredients[type]--;
      const newPrice = this.state.totalPrice - INGTREDIENT_PRICE[type];

      this.setState({
        purchasing: newPrice > 1000,
        totalPrice: newPrice,
        ingredients: newIngredients,
      });
    }
  };
  render() {
    const disabledIngredientsMinus = { ...this.state.ingredients };
    const disabledIngredientsAdd = { ...this.state.ingredients };
    //object dotor bdag propertygaar datwalt hiih
    for (let key in disabledIngredientsMinus) {
      disabledIngredientsMinus[key] = disabledIngredientsMinus[key] === 0;
    }
    for (let key in disabledIngredientsAdd) {
      disabledIngredientsAdd[key] = disabledIngredientsAdd[key] === 5;
    }
    return (
      <div>
        <Modal
          closeConfirmModal={this.closeConfirmModal}
          show={this.state.confirmOrder}
        >
          <OrderSummary
            onCancel={this.closeConfirmModal}
            onContinue={this.continueOrder}
            price={this.state.totalPrice}
            ingredientsName={INGTREDIENT_NAME}
            ingredients={this.state.ingredients}
          />
        </Modal>
        <Burger ingredients={this.state.ingredients} />
        <BuildControls
          showConfirmModal={this.showConfirmModal}
          ingredientsName={INGTREDIENT_NAME}
          disabledPurchasing={!this.state.purchasing}
          price={this.state.totalPrice}
          disabledIngredientsMinus={disabledIngredientsMinus}
          disabledIngredientsAdd={disabledIngredientsAdd}
          addIngredient={this.addIngredient}
          minusIngredient={this.minusIngredient}
        />
      </div>
    );
  }
}
export default BurgerPage;
